export default {
    mounted(element) {
        const options = {
            rootMargin: '0px',
            threshold: 1.0
        }
        
        const callback = (entries, observer) => {
            if (entries[0].isIntersecting && this.page < this.totalPages) {
            this.loadMorePosts()
            }
        };
        const observer = new IntersectionObserver(callback, options);
        observer.observe(element) 
    }
}
